import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";

const AdRecommendations = () => {
  const [userID, setUserID] = useState("user"); // Default user ID
  const [ads, setAds] = useState([]);
  const [error, setError] = useState("");

  const fetchAds = useCallback(() => {
    axios
      .get(`http://localhost:8082/recommend?userID=${userID}`)
      .then((response) => {
        if (Array.isArray(response.data)) {
          setAds(response.data);
          setError("");
        } else {
          setAds([]);
          setError("Invalid data received from server.");
        }
      })
      .catch((error) => {
        setAds([]);
        setError("Failed to fetch recommendations.");
        console.error("Error:", error);
      });
  }, [userID]);

  useEffect(() => {
    fetchAds();
  }, [fetchAds]); // Add fetchAds to the dependency array

  return (
    <div>
      <h2>Recommended Ads</h2>
      <label>
        User ID:
        <input
          type="text"
          value={userID}
          onChange={(e) => setUserID(e.target.value)}
        />
      </label>
      <button onClick={fetchAds}>Fetch Ads</button>
      {error && <p style={{ color: "red" }}>{error}</p>}
      <ul>
        {ads.map((ad) => (
          <li key={ad.ad_id}>
            <strong>{ad.category}:</strong> {ad.description}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdRecommendations;
