import React, { useState } from "react";
import axios from "axios";
import MoviePlayback from "./MoviePlayback"; // Adjust path if necessary

const AdRecommendations = () => {
  const [userID, setUserID] = useState(""); // State for user ID input
  const [ads, setAds] = useState([]);
  const [error, setError] = useState("");

  const fetchAds = (event) => {
    event.preventDefault(); // Prevent form submission from refreshing the page

    if (!userID) {
      setError("Please enter a user ID.");
      return;
    }

    axios
      .get(`http://localhost:8082/recommend?userID=${userID}`)
      .then((response) => {
        setAds(response.data);
        setError("");
      })
      .catch((error) => {
        setError("Failed to fetch recommendations.");
        console.error("Error:", error);
      });
  };

  // Handle playback updates (to re-fetch recommendations after playback)
  const handlePlaybackUpdate = (updatedUserData) => {
    setAds(updatedUserData.recommendations); // Assuming response contains updated recommendations
  };

  return (
    <div>
      <h2>Recommended Ads</h2>
      <form onSubmit={fetchAds}>
        <label htmlFor="userID">User ID:</label>
        <input
          type="text"
          id="userID"
          value={userID}
          onChange={(e) => setUserID(e.target.value)}
          placeholder="Enter user ID"
        />
        <button type="submit">Fetch Ads</button>
      </form>
      {error && <p style={{ color: "red" }}>{error}</p>}
      <ul>
        {ads.map((ad) => (
          <li key={ad.ad_id}>
            <strong>{ad.category}:</strong> {ad.description}
          </li>
        ))}
      </ul>

      {/* Include the MoviePlayback component */}
      <MoviePlayback userID={userID} onPlaybackUpdate={handlePlaybackUpdate} />
    </div>
  );
};

export default AdRecommendations;
