import React, { useState } from "react";
import axios from "axios";

const MoviePlayback = () => {
  const [userID, setUserID] = useState("");
  const [category, setCategory] = useState("");
  const [responseMessage, setResponseMessage] = useState("");

  const handlePlaybackSubmit = (e) => {
    e.preventDefault();

    // Send playback data to the backend
    axios
      .post("http://localhost:8082/playback", { userID, category })
      .then((response) => {
        setResponseMessage(`Playback data submitted: ${response.data}`);
      })
      .catch((error) => {
        setResponseMessage("Playback update failed.");
        console.error("Error:", error);
      });
  };

  return (
    <div>
      <h2>Simulate Movie Playback</h2>
      <form onSubmit={handlePlaybackSubmit}>
        <div>
          <label htmlFor="userID">User ID:</label>
          <input
            type="text"
            id="userID"
            value={userID}
            onChange={(e) => setUserID(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="category">Category:</label>
          <select
            id="category"
            value={category}
            onChange={(e) => setCategory(e.target.value)}
            required
          >
            <option value="">Select a category</option>
            <option value="Action">Action</option>
            <option value="Comedy">Comedy</option>
            <option value="Drama">Drama</option>
            <option value="Horror">Horror</option>
          </select>
        </div>
        <button type="submit">Submit Playback</button>
      </form>
      {responseMessage && <p>{responseMessage}</p>}
    </div>
  );
};

export default MoviePlayback;
