import React, { useState, useEffect } from "react";
import axios from "axios";

const AdRecommendations = () => {
  const [userID, setUserID] = useState("user"); // Default user ID
  const [ads, setAds] = useState([]); // Initialize ads as an empty array
  const [error, setError] = useState("");

  // Fetch Ads from the backend
  const fetchAds = () => {
    axios
      .get(`http://localhost:8082/recommend?userID=${userID}`)
      .then((response) => {
        if (Array.isArray(response.data)) {
          setAds(response.data); // Set ads only if the response is an array
          setError("");
        } else {
          setAds([]); // Reset ads if the response is not an array
          setError("Invalid data received from server.");
        }
      })
      .catch((error) => {
        setAds([]); // Reset ads on error
        setError("Failed to fetch recommendations.");
        console.error("Error:", error);
      });
  };

  // Fetch ads when the component mounts or userID changes
  useEffect(() => {
    fetchAds();
  }, [userID]);

  return (
    <div>
      <h2>Recommended Ads</h2>

      {/* User ID Input */}
      <label htmlFor="userID">User ID:</label>
      <input
        type="text"
        id="userID"
        value={userID}
        onChange={(e) => setUserID(e.target.value)}
        placeholder="Enter User ID"
      />

      {/* Fetch Ads Button */}
      <button onClick={fetchAds}>Fetch Ads</button>

      {/* Error Message */}
      {error && <p style={{ color: "red" }}>{error}</p>}

      {/* Ads List */}
      <ul>
        {ads.length > 0 ? (
          ads.map((ad) => (
            <li key={ad.ad_id}>
              <strong>{ad.category}:</strong> {ad.description}
            </li>
          ))
        ) : (
          <p>No ads available for the current user profile.</p>
        )}
      </ul>
    </div>
  );
};

export default AdRecommendations;
