import React, { useState } from "react";
import axios from "axios";

const MoviePlayback = ({ userID, onPlaybackUpdate }) => {
  const [movieID, setMovieID] = useState(""); // Movie ID for playback simulation
  const [category, setCategory] = useState(""); // Category of the ad or movie
  const [error, setError] = useState("");

  const handlePlayback = (event) => {
    event.preventDefault();

    if (!movieID || !category || !userID) {
      setError("Please enter a movie ID, category, and user ID.");
      return;
    }

    // Simulating playback of a movie and updating the user interests
    axios
      .post(`http://localhost:8082/playback`, {
        userID: userID,
        category: category,  // Send category as part of the request
      })
      .then((response) => {
        setError(""); // Clear error message if request is successful
        onPlaybackUpdate(response.data); // Call the parent component to update state (if needed)
      })
      .catch((error) => {
        setError("Failed to simulate playback.");
        console.error("Error:", error);
      });
  };

  return (
    <div>
      <h2>Simulate Movie Playback</h2>
      <form onSubmit={handlePlayback}>
        <label htmlFor="movieID">Movie ID:</label>
        <input
          type="text"
          id="movieID"
          value={movieID}
          onChange={(e) => setMovieID(e.target.value)}
          placeholder="Enter movie ID"
        />
        <br />
        <label htmlFor="category">Category:</label>
        <input
          type="text"
          id="category"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
          placeholder="Enter category"
        />
        <br />
        <button type="submit">Simulate Playback</button>
      </form>
      {error && <p style={{ color: "red" }}>{error}</p>}
    </div>
  );
};

export default MoviePlayback;
